// This script assumes 'items' is an array of objects from the previous Set node,
// where each object has 'item.json.formattedSummary' (the AI's output with CATEGORY: prefixes)
// and 'item.json.emailId'.

const items = $input.all();

const allCategorizedData = {
    news: [],
    tools: [],
    blogsArticles: [], // Changed from blogs to blogsArticles for clarity
    tipsTutorials: [], // Changed from tips to tipsTutorials
    insights: [],
    uncategorized: [] // For items the AI didn't categorize or we couldn't parse
};

const globallySeenUrls = new Set(); // For de-duplication across all categories

// Helper function to extract URLs
function extractUrls(text) {
    if (!text) return [];
    const urlRegex = /https?:\/\/(?:www\.)?[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b(?:[-a-zA-Z0-9()@:%_\+.~#?&//=]*)/g;
    const matches = text.match(urlRegex);
    return matches ? Array.from(new Set(matches)) : [];
}

for (const item of items) {
    if (!item.json.formattedSummary) {
        continue;
    }

    // The AI's summary for ONE newsletter (item.json.formattedSummary)
    // The AI was asked to prefix its "## Newsletter Title" before its content.
    // We expect its content to now have "CATEGORY: Xxx - ..." lines.
    const rawAiSummary = item.json.formattedSummary;

    // Attempt to get the newsletter title (if the AI included it as per our older Set node format)
    const titleMatch = rawAiSummary.match(/^## (.*)/m);
    const newsletterSourceTitle = titleMatch ? titleMatch[1] : "Unknown Newsletter";

    // Get content after the title and before the "---" separator if they exist
    let contentToParse = rawAiSummary;
    if (titleMatch) {
        contentToParse = contentToParse.substring(titleMatch[0].length).trim();
    }
    contentToParse = contentToParse.replace(/---$/, '').trim();

    const lines = contentToParse.split('\n').map(line => line.trim()).filter(line => line.length > 0);

    for (const line of lines) {
        let categorized = false;
        const itemContent = {
            text: line, // Default to full line
            urls: extractUrls(line),
            source: newsletterSourceTitle // Add source for potential display
        };

        // Check for URLs and de-duplicate based on the first URL if multiple
        let uniqueByUrl = true;
        if (itemContent.urls.length > 0) {
            if (globallySeenUrls.has(itemContent.urls[0])) { // Check primary URL for duplication
                uniqueByUrl = false;
            }
        }
        // If you want to check ALL URLs in an item for duplication:
        // let hasNovelUrl = false;
        // if (itemContent.urls.length > 0) {
        //   for (const url of itemContent.urls) {
        //     if (!globallySeenUrls.has(url)) {
        //       hasNovelUrl = true;
        //       break;
        //     }
        //   }
        //   if (!hasNovelUrl) uniqueByUrl = false;
        // }


        if (!uniqueByUrl) {
            continue; // Skip if this item (based on its primary URL) has already been seen
        }

        // Add URLs from this novel item to the global set
        itemContent.urls.forEach(url => globallySeenUrls.add(url));


        if (line.startsWith("CATEGORY: News - ")) {
            itemContent.text = line.substring("CATEGORY: News - ".length).trim();
            allCategorizedData.news.push(itemContent);
            categorized = true;
        } else if (line.startsWith("CATEGORY: Tool - ")) {
            itemContent.text = line.substring("CATEGORY: Tool - ".length).trim();
            allCategorizedData.tools.push(itemContent);
            categorized = true;
        } else if (line.startsWith("CATEGORY: Blog/Article - ")) {
            itemContent.text = line.substring("CATEGORY: Blog/Article - ".length).trim();
            allCategorizedData.blogsArticles.push(itemContent);
            categorized = true;
        } else if (line.startsWith("CATEGORY: Tip/Tutorial - ")) {
            itemContent.text = line.substring("CATEGORY: Tip/Tutorial - ".length).trim();
            allCategorizedData.tipsTutorials.push(itemContent);
            categorized = true;
        } else if (line.startsWith("CATEGORY: Insight - ")) {
            itemContent.text = line.substring("CATEGORY: Insight - ".length).trim();
            allCategorizedData.insights.push(itemContent);
            categorized = true;
        }

        if (!categorized && line.length > 10) { // If not categorized but seems like content
            // To avoid adding the original "## Newsletter Title" or "---" if they slipped through
            if (!line.startsWith("## ") && line !== "---") {
                 allCategorizedData.uncategorized.push(itemContent);
            }
        }
    }
}

// 3. Assemble the final digest in Markdown format
let finalDigestMarkdown = `# Daily AI Digest - ${new Date().toLocaleDateString('en-CA')}\n\n`; // Main title

const categoryOrder = [
    { key: 'news', title: 'ðŸ“° AI News & Announcements' },
    { key: 'tools', title: 'ðŸ› ï¸ New Tools & Platforms' },
    { key: 'blogsArticles', title: 'ðŸ“„ Blogs, Articles & Research' },
    { key: 'tipsTutorials', title: 'ðŸ’¡ AI Tips & Tutorials' },
    { key: 'insights', title: 'âœ¨ Other Valuable Insights' },
    { key: 'uncategorized', title: 'ðŸ“Ž Uncategorized Items' }
];

for (const category of categoryOrder) {
    const itemsInCategory = allCategorizedData[category.key];
    if (itemsInCategory.length > 0) {
        finalDigestMarkdown += `## ${category.title}\n\n`;
        for (const entry of itemsInCategory) {
            // Include source if you want: (from ${entry.source})
            finalDigestMarkdown += `*   ${entry.text}\n`;
        }
        finalDigestMarkdown += `\n`; // Add a newline after the list for spacing
    }
}

if (finalDigestMarkdown.length < 100) { // Arbitrary length to check if anything was added
    finalDigestMarkdown = "# Daily AI Digest\n\nNo new unique AI newsletter content found today, or content could not be categorized.";
}

// Collect all email IDs that were processed (useful for marking as read)
const emailIdsToMarkRead = items.map(item => item.json.emailId).filter(id => id);

return [{
    json: {
        finalDigest: finalDigestMarkdown,
        emailIdsToMarkRead: emailIdsToMarkRead
    }
}];
